def html(html_str, tag="", style="", bold=False, italic=False, font_size=''): 
    if bold: 
        style+='font-weight:bold;' 
    if italic: 
        style+='font-style:italic;' 
    if font_size: 
        style+=f'font-size:{font_size}px;' 
    if not tag and style: 
        tag = 'span' 
    if tag: 
        display(HTML("<" + tag + " style='" + style + "'" + ">" + html_str + "</" + tag + ">")) 
    else: 
        display(HTML(html_str))

def h1(html_str):
    html('<h1>%s</h1>' % html_str)

def h2(html_str):
    html('<h2>%s</h2>' % html_str)

def h3(html_str):
    html('<h3>%s</h3>' % html_str)

def h4(html_str):
    html('<h4>%s</h4>' % html_str)

def h5(html_str):
    html('<h4>%s</h4>' % html_str)

def bold(html_str, breakline='<br/>'):
    html(f'<b>{html_str}</b>{breakline}')

def breakline():
    html('<br/>')

