from io import StringIO
def df_to_html(df, **kwargs):

    pop_up=get_display_attr(df, 'pop_up')
    highlight_rows = get_display_attr(df, 'highlight_rows', default=None)
    ignore_cols = get_display_attr(df, 'ignore_cols', default=[])
    td_highlight_style = get_display_attr(df, 'td_highlight_style', default='')
    index = get_display_attr(df, 'index', default=True)
    max_rows = get_display_attr(df, 'max_rows', default=250)
    row_per_page = get_display_attr(df, 'row_per_page', default=250)
    max_cols = get_display_attr(df, 'max_cols', default=100)
    precision = get_display_attr(df, 'precision', default=3)
    tree_options = get_display_attr(df, 'tree_options')
    no_dimension_limits = get_display_attr(df, 'no_dimension_limits')

    table_html = StringIO()

    if index and str(df.index.dtype) == 'float64' or str(df.index.dtype) == 'int64':
        df.index += 1

    table_style, td_style, th_style, tr_even_style = '', '', '', ''
    if highlight_rows is not None and not td_highlight_style:
        td_highlight_style = '%s;font-weight: bold;' % td_style

    table_id = str(uuid.uuid1())
    table_html.write("""
        <div id="%s_outer_wrapper">
            <div class="table_div" id="%s_wrapper">
                <table style="%s" id="%s">""" % (table_id, table_id, table_style, table_id))
    table_html.write('<thead><tr class="data_table_header_row">')
    if index:
        index_name = ''
        if df.index.name:
            index_name = df.index.name
        table_html.write('<th class="index_cell" style="%s;">%s</th>' % (th_style, index_name))
    for col_count, col in enumerate(df.columns):
        if col in ignore_cols or col_count > max_cols:
            continue
        table_html.write("""
            <th class="data_table_header_cell" style="%s">
                <div class="col_%s_outer col_outer thead_div" col_id="%s" style="width:100%%;height:100%%">
                    <div class="col_%s_inner col_inner">%s</div>
                </div>
            </th>""" % (th_style, col_count, col_count, col_count, col))
    table_html.write('</tr></thead>')
    table_html.write('<tbody>')
    for count, row in enumerate(df.iterrows()):
        if count > max_rows:
            continue

        row_id = table_id + "_" + str(count)
        tr_class = 'table_row'
        if (count % 2) == 1:
            tr_class += ' even_row'

        tree_display, id_attr, parent_id_attr = False, '', ''
        if tree_options:
            tree_display = True
            id_attr = 'data-tt-id="%s"' % row[1][tree_options['id_col']] 
            if not pd.isnull(row[1][tree_options['parent_id_col']]):
                parent_id_attr = 'data-tt-parent-id="%s"' % int(row[1][tree_options['parent_id_col']])

        table_html.write('<tr id="%s" class="data_table_row %s" %s %s>' % (row_id, tr_class, id_attr, parent_id_attr))

        if index:
            table_html.write('<th class="index_cell" style="%s;font-weight:bold;" row_id="%s">%s</th>' % (td_style, row_id, row[0]))
        for col_count, val in enumerate(row[1]):

            if df.columns[col_count] in ignore_cols or col_count > max_cols:
                continue

            cell_id = row_id + "_" + str(col_count)

            alignment_style = '' 
            if is_numeric_dtype(df[df.columns[col_count]]):
                alignment_style = 'text-align:right;'

            td_style_local = td_style
            if highlight_rows is not None and highlight_rows[count]:
                td_style_local = td_highlight_style

            div_class = ''
            if str(df.dtypes[col_count]) == 'datetime64[ns]':
                if not val or pd.isnull(val):
                    value_to_print = ''
                else:
                    div_class += ' fixed_width'
                    value_to_print = val.to_datetime().strftime('%Y-%m-%d %H:%M:%S')
            elif str(df.dtypes[col_count]) == 'float64':
                if not val or not np.isnan(val):
                    # print integers without decimals
                    if (val*10 % 10) == 0:
                        value_to_print = str(int(val))
                    else:
                        value_to_print = ("{:.%sf}" % precision).format(val)
                else:
                    value_to_print = ''
            elif str(df.dtypes[col_count]) == 'int64':
                if not val or not np.isnan(val):
                    value_to_print = int(val)
                else:
                    value_to_print = ''
            else:
                if val:
                    value_to_print = str(val).replace('\n', '<br/>')
                    div_class += 'limited_height limited_width'
                else:
                    value_to_print = ''

            if no_dimension_limits:
                div_class = '' 

            table_html.write("""
                    <td id="%s" style="%s;%s" class="col_%s">
                        <span class="col_%s_outer col_outer" col_id="%s" style="width:100%%;height:100%%">
                            <span class="col_%s_inner col_inner %s">%s</span>
                        </span>
                    </td>
                """
                % (cell_id, td_style_local, alignment_style, col_count, col_count, col_count, col_count, div_class, value_to_print))

        table_html.write('</tr>')
    table_html.write('</tbody>')
    table_html.write('</table></div></div>')
    table_html.write("""
        <script>

            var tableId = '%s'
            var tableIdSelector = '#' + tableId

            $(tableIdSelector + " .index_cell").click(function() {
                row_id = $(this).attr('row_id')
                $("#" + row_id).toggleClass('selected_row')
            });

            $(tableIdSelector + " td .col_outer")
            .click(function() {
                $(this).children('.col_inner').toggleClass('limited_height')
                $(this).children('.col_inner').toggleClass('free_height')
            })

            var pop_up = '%s'
            if (pop_up == 'True') {
                $( tableIdSelector + "_wrapper" ).css('max-height', $( window ).height()*0.9 + 'px');            
                $( tableIdSelector + "_outer_wrapper" ).dialog({'width': $( window ).width()*0.9, 'height': $( window ).height()*0.9});            
            }

            var tree_display = '%s'
            if (tree_display == 'True') {
                $(tableIdSelector).treetable({ expandable: true, initialState: 'expanded'});
            }

            document.getElementById(tableId + "_wrapper").addEventListener("scroll",function(){
               var translate = "translate(0,"+this.scrollTop+"px)";
               this.querySelector("thead").style.transform = translate;
            });

        </script>
    """ % (table_id, pop_up, tree_display))

    if index and str(df.index.dtype) == 'float64' or str(df.index.dtype) == 'int64':
        df.index -= 1
    return table_html.getvalue()


