import json
def df_to_html2(df, **kwargs):

    pop_up=get_display_attr(df, 'pop_up')
    highlight_rows = get_display_attr(df, 'highlight_rows', default=[])
    ignore_cols = get_display_attr(df, 'ignore_cols', default=[])
    td_highlight_style = get_display_attr(df, 'td_highlight_style', default='')
    index = get_display_attr(df, 'index', default=True)
    max_rows = get_display_attr(df, 'max_records_to_display', default=500)
    rows_per_page = get_display_attr(df, 'rows_per_page', default=20)
    max_cols = get_display_attr(df, 'max_cols', default=100)
    precision = get_display_attr(df, 'precision', default=3)
    tree_options = get_display_attr(df, 'tree_options')
    #if tree_options:
    #    return df_to_html(df, **kwargs)
    no_dimension_limits = get_display_attr(df, 'no_dimension_limits')

    df_orig_shape = df.shape
    df = df.iloc[:max_rows].copy()
    if index and str(df.index.dtype) == 'float64' or str(df.index.dtype) == 'int64':
        df.index += 1

    index_col = {'title': '', 'field':'_index', 'frozen':True}
    df['_index'] = df.index
    column_config = [index_col, ] 
    # handle case of columns with same name, OrderedDict to keep the order of keys same
    # from collections import OrderedDict
    # cols_map = OrderedDict([(col, 1) for col in df.columns])
    # cols = [col for col in cols_map.keys()]
    # df = df[cols]
    ignored_cols = set()
    for col in df.columns:
        col_config = {'title': col, 'field': col, 'align':'left'}
        if col in ignored_cols:
            continue
        if type(df[col]) == pd.DataFrame:
            df.drop(col, axis=1, inplace=True)
            log_error('Columns %s duplicated, not displaying' % col) 
            ignored_cols.add(col)
            continue
        if str(df[col].dtype) == 'float64':
            col_config['align'] ='right'
            df[col] = df[col].map(' {:,.2f} '.format)
        elif str(df[col].dtype) == 'int64':
            df[col] = df[col].astype(str)
        elif str(df[col].dtype) == 'datetime64[ns]': # timedelta64[ns]
            df[col] = df[col].dt.strftime('%Y-%m-%d %H:%M:%S')
        else:
            max_chars = df[col].astype(str).str.len().max()
            if max_chars > 30:
                col_config['width'] = 150
            df[col] = ' ' + df[col].astype(str) + ' '
            df.loc[df[col] == ' None ', col] = ' '

        if col == '_index':
            continue
        column_config.append(col_config)
    column_config_json = json.dumps(column_config)

    table_html = StringIO()

    table_div_id = str(uuid.uuid1())
    table_html.write("""<div>%sx%s read (%sx%s rows being displayed)</div><div id="%s"></div>"""\
    % (df_orig_shape[0], df_orig_shape[1], df.shape[0], df.shape[1] - 1, table_div_id))

    table_html.write("""
        <script>
            var tableDivId = '%s'
            var tableDivIdSelector = '#' + tableDivId
            $(tableDivIdSelector).tabulator({
                tooltips:true,
                columns:%s,
                pagination:"local",
                paginationSize:%s,

                tableBuilt:function() {
                    setTimeout(function() {
                        $('.tabulator-col-title').dblclick(function(event) {
                            copyToClipboard($(this))
                            event.stopImmediatePropagation();
                            $(this).focus()
                            $(this).select()
                        });
                    }, 2000)                
                },
            });

            var tabledata = %s
            $(tableDivIdSelector).tabulator("setData", tabledata);
            
        </script>
    """ % (table_div_id, column_config_json, rows_per_page, df.to_json(orient='records', date_format='iso', double_precision=3)))

    if index and str(df.index.dtype) == 'float64' or str(df.index.dtype) == 'int64':
        df.index -= 1

    return table_html.getvalue()


def get_display_attr(df, attr, default=None):
    if (attr in df.display_info.__dict__):
        return df.display_info.__dict__[attr]
    df.display_info.__dict__[attr] = default 
    return default

def to_html(self, **kwargs):
    return df_to_html2(self, **kwargs)

from pandas import DataFrame, Series
if not 'to_html_new' in DataFrame.__dict__:
    DataFrame.to_html_orig = DataFrame.to_html
    DataFrame.to_html_new = to_html

def df_display_table(self, **kwargs):
    if 'float_format' in kwargs:
        float_format = kwargs['float_format']
    else:
        float_format='%.3f'

    if notebook_mode:
        # if console_log is on, it means notebook_mode is on, so we need to print HTML
        html(self.to_html_new(**kwargs))
    else:
        # else just print a CSV
        log_info(self.to_csv(float_format=float_format))

def df_display(self, **kwargs):
    if 'float_format' in kwargs:
        float_format = kwargs['float_format']
    else:
        float_format='%.3f'

    if notebook_mode:
        # if console_log is on, it means notebook_mode is on, so we need to print HTML
        html(self.to_html(**kwargs))
    else:
        # else just print a CSV
        log_info(self.to_csv(float_format=float_format))
